/**
 * For parsing strings like : 
 * click ( '12', "zoo", 14, true, blur(2, 4.02, hello( false).foo())). bar(12345)
 *
 * see tests for result
 */

import elenpi from '../index.js';

const r = elenpi.Rule.initializer,
	rules = {
		doublestring: r.terminal(/^"([^"]*)"/, (env, obj, cap) => obj.value = cap[1]),

		singlestring: r.terminal(/^'([^']*)'/, (env, obj, cap) => obj.value = cap[1]),

		float: r.terminal(/^[0-9]*\.[0-9]+/, (env, obj, cap) => obj.value = parseFloat(cap[0], 10)),

		integer: r.terminal(/^[0-9]+/, (env, obj, cap) => obj.value = parseInt(cap[0], 10)),

		bool: r.terminal(/^(true|false)/, (env, obj, cap) => obj.value = (cap[1] === 'true') ? true : false),

		//_____________________________________

		calls: r
			.space()
			.zeroOrMore({
				rule: 'call',
				pushTo: 'calls',
				separator: r.terminal(/^\s*\.\s*/)
			}),

		call: r
			.terminal(/^([\w-_]+)\s*/, (env, obj, cap) => obj.method = cap[1])
			.terminal(/^\(\s*/)
			.zeroOrMore({
				rule: r.oneOf('integer', 'bool', 'singlestring', 'doublestring', 'calls'),
				pushTo: 'arguments',
				separator: r.terminal(/^\s*,\s*/)
			})
			.terminal(/^\s*\)/)
	};

export default new elenpi.Parser(rules, 'calls');

/** 
 * @author Gilles Coomans <gilles.coomans@gmail.com>
 * html tokenizer
 */

import elenpi from '../index.js';
const r = elenpi.Rule.initializer,
	Parser = elenpi.Parser,
	exec = Parser.exec,
	attributeExpr = /^([\w-_]+)\s*(?:=\s*("([^"]*)"|[\w-_]+))?\s*/,
	openTags = /^(br|input|img|area|base|col|command|embed|hr|img|input|keygen|link|meta|param|source|track|wbr)/,
	onlySpace = /^\s+$/,
	rawContentTags = /^(?:script|style|code)/;

const rules = {
	document: r
		.zeroOrMore(r.space().one('comment'))
		.terminal(/^\s*<!DOCTYPE[^>]*>\s*/i)
		.one('children')
		.space(),

	comment: r.terminal(/^<!--([\s\S]*)?(?=-->)-->/, (env, obj, cap) => obj.comment = cap[1]),

	// closing tag
	tagEnd: r.terminal(/^\s*<\/([\w-_\:]+)\s*>/, (env, obj, cap) => {
		if (obj.tagName !== cap[1]) {
			env.errors = env.errors || [];
			env.errors.push('tag badly closed : ' + cap[1] + ' - (at opening : ' + obj.tagName + ')');
		}
	}),

	// tag children
	children: r
		.zeroOrMore({
			pushTo: 'children',
			rule: r.oneOf('comment', 'text', 'tag')
		}),

	text: r.terminal(/^[^<]+/, (env, obj, cap) => {
		const val = cap[0];
		if (onlySpace.test(val)) 
			obj.skip = true;
		else 
			obj.textValue = val;
	}),

	// normal tag (including raw tags)
	tag: r
		// start tag
		.terminal(/^<([\w-_\:]+)\s*/, (env, obj, cap) => obj.tagName = cap[1])
		// attributes
		.zeroOrMore(
			// attrName | attrName="... ..." | attrName=something | attrName={{ .. }} | attrName={ .. }
			// with an optional space (\s*) after equal sign (if any).
			r.terminal(attributeExpr, (env, obj, cap) => {
				const attrName = cap[1],
					value = (cap[3] !== undefined) ? cap[3] : ((cap[2] !== undefined) ? cap[2] : '');
				switch (attrName) {
					case 'class':
						if (!value)
							break;
						obj.classes = value.split(/\s+/);
						break;
					default:
						obj.attributes = obj.attributes || {};
						obj.attributes[attrName] = value;
						break;
				}
			})
		)
		.oneOf(
			r.char('>')
			.done((env, obj) => {
				// check html5 unstrict self-closing tags
				if (openTags.test(obj.tagName))
					return; // no children

				if (rawContentTags.test(obj.tagName)) // get raw content
					return rawContent(obj.tagName, env, obj);

				// get inner tag content
				exec(env.parser.rules.children, obj, env);
				if (!env.error) // close tag
					exec(env.parser.rules.tagEnd, obj, env);
			}),
			// strict self closed tag
			r.terminal(/^\/>/),
			r.error('Missing end of tag')
		)
};

// raw inner content of tag
function rawContent(tagName, env, tag) {
	const index = env.string.indexOf('</' + tagName + '>');
	if (index === -1) {
		env.error = true;
		env.errorMessage = tagName + ' tag not closed.';
		return;
	}
	if (index) // more than 0
		tag.rawContent = env.string.substring(0, index);
	env.string = env.string.substring(index + tagName.length + 3);
}

const parser = new Parser(rules, 'children');

export default parser;

/**
 * @author Gilles Coomans <gilles.coomans@gmail.com>
 * elenpi rules for primitives function's arguments. aka : "double", 'single', 1.12, 14, true, false
 */
import elenpi from '../index.js';
const r = elenpi.Rule.initializer;

/**
 * example of rules for parsing arguments somewhere
 * @type {Object}
 * @private
 */
const rules = {
	doublestring: r.terminal(/^"([^"]*)"/, (env, descriptor, cap) => descriptor.arguments.push(cap[1])),

	singlestring: r.terminal(/^'([^']*)'/, (env, descriptor, cap) => descriptor.arguments.push(cap[1])),

	float: r.terminal(/^[0-9]*\.[0-9]+/, (env, descriptor, cap) => descriptor.arguments.push(parseFloat(cap[0], 10))),

	integer: r.terminal(/^[0-9]+/, (env, descriptor, cap) => descriptor.arguments.push(parseInt(cap[0], 10))),

	bool: r.terminal(/^(true|false)/, (env, descriptor, cap) => descriptor.arguments.push((cap[1] === 'true') ? true : false))
};

export default rules;

export default {
	doublestring: /^"([^"]*)"/,
	singlestringArg: /^'([^']*)'/,
	floatArg: /^[0-9]*\.[0-9]+/,
	integerArg: /^[0-9]+/,
	boolArg: /^(true|false)/,
	id: /^[\w-_]+/,
	objectKey: /^([\w-_]+)|"([^"]*)"|'([^']*)'/
};

/**
 * elenpi
 * @author Gilles Coomans
 */

import Parser from './parser.js';
import Rule from './rule.js';

/**
 * elenpi export object { Rule, Parser }
 * @type {Object}
 * @public
 */
const elenpi = {
	Rule,
	Parser
};

export default elenpi;

/*
* @Author: Gilles Coomans
* @Last Modified by:   Gilles Coomans
* @Last Modified time: 2017-05-07 23:42:17
*/

/**
 * The Parser class.
 * @public
 */
class Parser {

	/**
	 * @param  {Object} rules       an object containing rules
	 * @param  {String} defaultRule the default rule to use when parsing
	 */
	constructor(rules, defaultRule) {

		/**
		 * the rules map
		 * @type {Object}
		 */
		this.rules = rules;

		/**
		 * the default rule's name to use
		 * @type {String}
		 */
		this.defaultRule = defaultRule;
	}

	/**
	 * find rule by name
	 * @param  {String} name the rule's name
	 * @return {Rule}      the finded rule
	 * @throws {Error} If rule not found
	 */
	getRule(name) {
		const r = this.rules[name];
		if (!r)
			throw new Error('elenpi parser : rules not found : ' + name);
		return r;
	}

	/**
	 * Parse provided string with specific rule
	 * @param  {String} string     the string to parse
	 * @param  {?String} rule       the name of the rule to apply. default is null (will use parser's default method if not provided).
	 * @param  {?Object} descriptor the main descriptor object
	 * @param  {?Object} env 		the env object (internal object used while parsing)
	 * @return {Object}            the decorated descriptor
	 * @throws {Error} If parsing fail (for any reason)
	 */
	parse(string, rule = null, descriptor = {}, env = null) {
		env = env || {};
		env.parser = this;
		env.string = string;
		rule = rule || this.getRule(this.defaultRule);
		Parser.exec(rule, descriptor, env);
		if (!env.error && env.string.length) {
			env.error = true;
			env.errorMessage = 'string wasn\'t parsed entierly';
		}
		if (env.error) {
			const pos = string.length - env.string.length,
				posInFile = getPositionInFile(string, pos);
			throw new Error('Parsing failed : ' + (env.errorMessage || 'no rules matched') + ' : (line:' + posInFile.line + ' , col:' + posInFile.col + ') near :\n', string.substring(pos, pos + 50));
		}
		return descriptor;
	}

	/**
	 * Execute a rule (only for those who developing grammars)
	 * @param  {String|Rule} rule      the name of the rule to use or the rule itself
	 * @param  {Object} descriptor the descriptor to decorate
	 * @param  {Object} env        the inner-job main object where parser, parsed string and eventual errors are stored
	 * @return {Void}            nothing
	 * @public
	 * @throws {Error} If rule is string (so it's a rule's name) and referenced rule could not be found with it.
	 */
	static exec(rule, descriptor, env) {
		if (env.error)
			return;
		if (typeof rule === 'string')
			rule = env.parser.getRule(rule);

		const rules = rule._queue;
		for (let i = 0, current, len = rules.length; i < len; ++i) {
			current = rules[i];
			if (current.__elenpi__)
				Parser.exec(current, descriptor, env);
			else // is function
				current(env, descriptor);
			if (env.error)
				break;
		}
	}
}

function getPositionInFile(string, position) {
	const splitted = string.split(/\r|\n/),
		len = splitted.length;
	let lineNumber = 0,
		current = 0,
		line,
		lineLength;
	while (lineNumber < len) {
		line = splitted[lineNumber];
		lineLength = line.length;
		if (position <= (current + lineLength))
			break;
		current += lineLength;
		lineNumber++;
	}
	return {
		line: lineNumber + 1,
		col: position - current
	};
}

export default Parser;

/*
 * @Author: Gilles Coomans
 */

import Parser from './parser.js';

const defaultSpaceRegExp = /^\s+/,
	exec = Parser.exec;

/**
 * The Rule base class.
 * @public
 */
class Rule {

	/**
	 * the Rule constructor
	 */
	constructor() {
		this._queue = [];
		this.__elenpi__ = true;
	}

	/**
	 * the base handler for every other lexems
	 * @param  {Function} callback the callback to handle string
	 * @return {Rule}          this rule handler
	 */
	done(callback) {
		this._queue.push(callback);
		return this;
	}

	/**
	 * use another rule  
	 * @param  {String|Rule|function} rule the rule to use
	 * @return {Rule}          this rule handler
	 */
	use(rule) {
		// const args = [].slice.call(arguments, 1);
		return this.done((env, descriptor) => {
			if (typeof rule === 'string')
				rule = env.parser.getRule(rule);
			if (rule.__elenpi__) {
				exec(rule, descriptor, env);
			}
			/* else if(typeof rule === 'function') {
				const r = new Rule();
				rule.apply(r, args);
				exec(r, descriptor, env);
			}*/
		});
	}

	/**
	 * catch a terminal
	 * @param  {RegExp} reg the terminal's regexp
	 * @param  {String|Function} set either the name of the property (in current descriptor) where store the catched value 
	 *                           or a function to handle captured object by hand 
	 * @return {Rule}          this rule handler
	 */
	terminal(reg, set) {
		return this.done((env, descriptor) => {
			if (!env.string.length) {
				env.error = true;
				return;
			}
			const cap = reg.exec(env.string);
			if (cap) {
				env.string = env.string.substring(cap[0].length);
				if (set) {
					if (typeof set === 'string')
						descriptor[set] = cap[0];
					else
						set(env, descriptor, cap);
				}
			} else
				env.error = true;
		});
	}

	/**
	 * match a single character
	 * @param  {String} test the caracter to match
	 * @return {Rule}          this rule handler
	 */
	char(test) {
		return this.done((env) => {
			if (!env.string.length || env.string[0] !== test)
				env.error = true;
			else
				env.string = env.string.substring(1);
		});
	}

	/**
	 * match x or more element from string with provided rule
	 * @param  {Rule|Object} rule either a rule instance or an option object
	 * @return {Rule}          this rule handler
	 */
	xOrMore(rule) {
		const opt = (typeof rule === 'string' || rule.__elenpi__) ? {
			rule
		} : rule;
		opt.minimum = opt.minimum || 0;
		opt.maximum = opt.maximum || Infinity;
		return this.done((env, descriptor) => {

			if (opt.minimum && !env.string.length) {
				env.error = true;
				return;
			}

			const rule = opt.rule,
				pushTo = opt.pushTo,
				pushToString = typeof pushTo === 'string',
				As = opt.as,
				separator = opt.separator;

			let count = 0,
				currentPosition,
				newDescriptor,
				restLength;

			while (env.string.length && count < opt.maximum) {

				newDescriptor = As ? As(env, descriptor) : (pushTo ? {} : descriptor);
				currentPosition = env.string.length;

				exec(rule, newDescriptor, env);

				restLength = env.string.length;

				if (env.error) {
					if (currentPosition === restLength) // has not moved deeper : so try next rule
						env.error = false;
					break;
				}

				count++;

				// store new descriptor in parent descriptor
				if (!newDescriptor.skip && pushTo)
					if (pushToString) {
						descriptor[pushTo] = descriptor[pushTo] || [];
						descriptor[pushTo].push(newDescriptor);
					} else
						pushTo(env, descriptor, newDescriptor);

				// manage separator
				if (separator && restLength) {
					currentPosition = restLength;
					exec(separator, newDescriptor, env);
					if (env.error) {
						if (currentPosition === env.string.length)
							env.error = false;
						break;
					}
				}
			}

			if (!env.error && count < opt.minimum) {
				env.error = true;
				env.errorMessage = "missing xOrMore item : " + rule;
			}
		});
	}

	/**
	 * match 0 or more element from string with provided rule
	 * @param  {Rule|Object} rule either a rule instance or an option object
	 * @return {Rule}          this rule handler
	 */
	zeroOrMore(rule) {
		return this.xOrMore(rule);
	}

	/**
	 * match 1 or more element from string with provided rule
	 * @param  {Rule|Object} rule either a rule instance or an option object
	 * @return {Rule}          this rule handler
	 */
	oneOrMore(rule) {
		if (typeof rule === 'string' || rule.__elenpi__)
			rule = {
				rule,
				minimum: 1
			};
		else
			rule.minimum = 1;
		return this.xOrMore(rule);
	}

	/**
	 * match one element from string with one of provided rules
	 * @param  {Rule|Object} rules either a rule instance or an option object
	 * @return {Rule}          this rule handler
	 */
	oneOf(rules) {
		const opt = (typeof rules === 'string' || rules.__elenpi__) ? {
			rules: [].slice.call(arguments)
		} : rules;
		return this.done((env, descriptor) => {

			if (!opt.optional && !env.string.length) {
				env.error = true;
				return;
			}

			const len = opt.rules.length,
				currentPosition = env.string.length;

			let count = 0,
				rule,
				newDescriptor;

			while (count < len) {
				rule = opt.rules[count++];
				newDescriptor = opt.as ? opt.as(env, descriptor) : (opt.set ? {} : descriptor);
				exec(rule, newDescriptor, env);
				if (env.error) {
					if (env.string.length === currentPosition) {
						env.error = false;
						continue;
					}
				} else
					setDescriptor(descriptor, newDescriptor, opt.set, env);
				return;
			}
			if (!opt.optional)
				env.error = true;
		});
	}

	/**
	 * maybe match one element from string with one of provided rules
	 * @param  {Rule|Object} rules either a rule instance or an option object
	 * @return {Rule}          this rule handler
	 */
	maybeOneOf(rules) {
		const opt = (typeof rules === 'string' || rules.__elenpi__) ? {
			rules: [].slice.call(arguments)
		} : rules;
		opt.optional = true;
		return this.oneOf(opt);
	}

	/**
	 * match one element from string with provided rule
	 * @param  {Rule|Object} rule either a rule instance or an option object
	 * @return {Rule}          this rule handler
	 */
	one(rule) {
		const opt = (typeof rule === 'string' || (rule && rule.__elenpi__)) ? {
			rule
		} : rule;
		return this.done((env, descriptor) => {
			if (!opt.optional && !env.string.length) {
				env.error = true;
				return;
			}
			const newDescriptor = opt.as ? opt.as(env, descriptor) : (opt.set ? {} : descriptor),
				currentPosition = env.string.length;

			exec(opt.rule, newDescriptor, env);
			if (!env.error)
				setDescriptor(descriptor, newDescriptor, opt.set, env);
			else if (opt.optional && env.string.length === currentPosition)
				env.error = false;
		});
	}

	/**
	 * maybe match one element from string with provided rule
	 * @param  {Rule|Object} rule either a rule instance or an option object
	 * @return {Rule}          this rule handler
	 */
	maybeOne(rule) {
		const opt = (typeof rule === 'string' || (rule && rule.__elenpi__)) ? {
			rule
		} : rule;
		opt.optional = true;
		return this.one(opt);
	}

	/**
	 * skip current descriptor
	 * @return {Rule}          this rule handler
	 */
	skip() {
		return this.done((env, descriptor) => {
			descriptor.skip = true;
		});
	}

	/**
	 * match a space (any spaces, or carriage returns, or new lines)
	 * @param  {Boolean} needed true if space is needed. false otherwise.
	 * @return {Rule}          this rule handler
	 */
	space(needed = false) {
		return this.done((env) => {
			if (!env.string.length) {
				if (needed)
					env.error = true;
				return;
			}
			const cap = (env.parser.rules.space || defaultSpaceRegExp).exec(env.string);
			if (cap)
				env.string = env.string.substring(cap[0].length);
			else if (needed)
				env.error = true;
		});
	}

	/**
	 * match the end of string
	 * @return {Rule}          this rule handler
	 */
	end() {
		return this.done((env) => {
			if (env.string.length)
				env.error = true;
		});
	}

	/**
	 * force end parsing with error. Only aimed to be used in .oneOf().
	 * @param  {String} msg the error message.
	 * @return {Rule}          this rule handler
	 */
	error(msg) {
		return this.done((env) => {
			env.error = true;
			env.errorMessage = msg;
		});
	}
}

function setDescriptor(descriptor, newDescriptor, set, env) {
	if (!newDescriptor.skip && set)
		if (typeof set === 'string')
			descriptor[set] = newDescriptor;
		else
			set(env, descriptor, newDescriptor);
}

const r = {};

Object.getOwnPropertyNames(Rule.prototype) // because Babel make prototype methods not enumerable
	.forEach((key) => {
		if (typeof Rule.prototype[key] === 'function')
			r[key] = function() {
				const rule = new Rule();
				return rule[key].apply(rule, arguments);
			};
	});

/**
 * Rule initializer object (all the Rul's API for starting rule's sentences)
 * @type {Object}
 * @public
 * @static
 */
Rule.initializer = r;

export default Rule;
